async () => {
        const tableData = await HomePage.getTableData(await HomePage.overViewTable);
        const expectedTableDataFromJson = getLatestInvoicePerVendor().map((invoice) => {
            const monthOrQuarterString = invoice.invoice_is_quarterly === "true"
                ? quarterName(invoice.month)
                : prettyMonthName(invoice.month);
            const reconciliationDetails = generateExpectedOverviewTableStatusFromPercentagePriceDifference(parseFloat(invoice.price_difference_percentage));
            return {
                contractName: invoice.contract_name,
                vendor: invoice.vendor_name,
                period: `${monthOrQuarterString} ${invoice.year}`,
                reconciliationDetails,
                details: "View Invoice",
            };
        });
        const sortedTableDataByVendor = tableData
            .map((data) => ({
            contractName: data["Contract name"].trim(),
            vendor: data.Vendor.trim(),
            period: data.Period.trim(),
            reconciliationDetails: data["Reconciliation status"]
                .trim()
                .toUpperCase(),
            details: data.Details.trim(),
        }))
            .sort((a, b) => a.vendor.localeCompare(b.vendor));
        const sortedExpectedData = Array.from(expectedTableDataFromJson).sort((a, b) => a.vendor.localeCompare(b.vendor));
        expect(sortedExpectedData).toEqual(sortedTableDataByVendor);
    }

async () => {
        const firstInvoice = getLatestInvoicePerVendor().sort((a, b) => a.contract_name.localeCompare(b.contract_name))[0];
        await HomePage.clickOnFirstContractInTable();
        await waitForPageLoad();
        expect(await browser.getUrl()).toContain(`${firstInvoice.contract_id}/invoices`);
    }

async () => {
        const firstInvoice = getLatestInvoicePerVendor().sort((a, b) => a.contract_name.localeCompare(b.contract_name))[0];
