async () => {
        await browser.url("/?foo=bar");
        await waitForPageLoad();
        expect(await HomePage.isPageHeadingDisplayed()).toBe(true);
    }

async () => {
        await browser.url("/?foo=<script>");
        await waitForPageLoad();
        await expect(async () => await HomePage.isPageHeadingDisplayed()).rejects.toThrow("Page heading was not displayed");
    }

